-- 1. CREATE TABLE
CREATE TABLE IF NOT EXISTS carriers (
  id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
  company_name TEXT NOT NULL,
  mc_number INTEGER UNIQUE,
  status TEXT DEFAULT 'Active',
  is_flagged BOOLEAN DEFAULT FALSE,
  contact_name TEXT,
  contact_phone TEXT,
  contact_email TEXT,
  city TEXT,
  state CHAR(2),
  fleet_power_units INTEGER DEFAULT 0,
  date_entered DATE,
  avg_rating DECIMAL(3,2),
  num_loads INTEGER DEFAULT 0,
  last_load_date DATE,
  carrier_lane_count INTEGER,
  lane_run_count INTEGER,
  lane_last_run_date DATE,
  created_at TIMESTAMPTZ DEFAULT NOW(),
  updated_at TIMESTAMPTZ DEFAULT NOW()
);

-- 2. CREATE INDEXES
CREATE INDEX IF NOT EXISTS idx_carriers_mc_number ON carriers(mc_number);
CREATE INDEX IF NOT EXISTS idx_carriers_state ON carriers(state);
CREATE INDEX IF NOT EXISTS idx_carriers_status ON carriers(status);
CREATE INDEX IF NOT EXISTS idx_carriers_name ON carriers(company_name);

-- 3. AUTO-UPDATE TRIGGER FOR updated_at
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
  NEW.updated_at = NOW();
  RETURN NEW;
END;
$$ LANGUAGE plpgsql;

DROP TRIGGER IF EXISTS update_carriers_updated_at ON carriers;
CREATE TRIGGER update_carriers_updated_at
  BEFORE UPDATE ON carriers
  FOR EACH ROW
  EXECUTE FUNCTION update_updated_at_column();

-- 4. ENABLE ROW LEVEL SECURITY
ALTER TABLE carriers ENABLE ROW LEVEL SECURITY;

-- Basic policy: authenticated users can do everything
CREATE POLICY "Allow authenticated access" ON carriers
  FOR ALL
  TO authenticated
  USING (true)
  WITH CHECK (true);

